-- ByteStrings
type BString = [Maybe Word8]

toBString :: ByteString -> BString
toBString bs = let ws = Data.ByteString.unpack bs in Data.List.map (\w -> Just w) ws ++ [Nothing]


flatten :: [[a]] -> [a]
flatten [] = []
flatten (xs:xss) = xs ++ flatten xss

